mapper.select:
select *
from kotoumi_daily
where group_id = #{groupId}
  and user_id = #{userId}
  and date(create_time) = #{day}

mapper.update:
update kotoumi_daily
set sign_in_result = #{signInResult}
where group_id = #{groupId}
  and user_id = #{userId}
  and date(create_time) = #{day}

update kotoumi_daily
set draw_result = #{drawResult}
where group_id = #{groupId}
  and user_id = #{userId}
  and date(create_time) = #{day}

update kotoumi_daily
set divine_result = #{divineResult}
where group_id = #{groupId}
  and user_id = #{userId}
  and date(create_time) = #{day}

mapper.select:
select sum(IF(sign_in_result != 0, 1, 0))
from kotoumi_daily
where group_id = #{groupId}
  and user_id = #{userId}
