mapper.insert:
insert into kotoumi_genshin_saint_wish (
    user_id, saint_name, pos, level, score, ratio, main_property, sub_properties
) values (
    #{userId}, #{saintName}, #{pos}, #{level}, #{score}, #{ratio}, #{mainProperty}, #{subProperties}
)

mapper.update:
update kotoumi_genshin_saint_wish
set level = #{level}, score = #{score}, ratio = #{ratio}, main_property = #{mainProperty}, sub_properties = #{subProperties}
where id = #{id}

mapper.select:
select count(1)
from kotoumi_genshin_saint_wish
where user_id = #{userId}
